// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('demo', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('firefox').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('demo', async function() {
    // Test name: demo
    // Step # | name | target | value | comment
    // 1 | open | / |  | 
    await driver.get("https://demo.applitools.com/")
    // 2 | setWindowSize | 1050x708 |  | 
    await driver.setRect(1050, 708)
    // 3 | verifyText | css=.auth-header | Login Form | 
    assert(await driver.findElement(By.css(".auth-header")).getText() == "Login Form")
    // 4 | click | id=username |  | 
    await driver.findElement(By.id("username")).click()
    // 5 | type | id=username | demo | 
    await driver.findElement(By.id("username")).sendKeys("demo")
    // 6 | type | id=password | demo | 
    await driver.findElement(By.id("password")).sendKeys("demo")
    // 7 | click | id=log-in |  | 
    await driver.findElement(By.id("log-in")).click()
    // 8 | assertTitle | ACME demo app |  | 
    assert(await driver.getTitle() == "ACME demo app")
    // 9 | close |  |  | 
    await driver.close()
  })
})
